//eslint-disable 

import logo from './logo.svg';
import './App.css';
import { useState } from 'react';

function App() {

  let post = '강남 우동 맛집';
  let [logo, setLogo] = useState('ReactBlog')
  let [a, changeTitle] = useState(['강남 우동 맛집','강남 초밥 맛집','강남 훠궈 맛집']) //자료 잠깐 저장,
  let [like, plusLike] = useState(0)

  //state 만드는법
  //a : 강남 우동 맛집
  //b : state 변경을 도와주는 함수
  //변수 넣을 땐 중괄호

  //변수와 state의 차이점
  //일반 변수는 값자기 변경되면 html에 자동으로 반영 안됨
  //state는 갑자기 변경되면 html이 자동으로 재렌더링됨
  //그렇다고 온갖것을 다 state로 만들지는 말기, 자주 변경될 것 같은 것만
  return (
    <div className="App">
      <div className="black-nav">
        <h4>{logo}</h4>
      </div>
      <div className="list">
        <h4>{a[0]} <span onClick={()=>{
          plusLike(like + 1)}
          }>👍</span> {like} </h4> 
        <p>2월 17일 발행</p>
      </div>

      <div className="black-nav">
        <h4>{logo}</h4>
      </div>
      <div className="list">
        <h4>{a[1]}</h4>
        <p>2월 17일 발행</p>
      </div>

      <div className="black-nav">
        <h4>{logo}</h4>
      </div>
      <div className="list">
        <h4>{a[2]}</h4>
        <p>2월 17일 발행</p>
      </div>


      <button onClick={()=>{
        let titleCopy = [...a];
        titleCopy[0] = '나 목요일에 누리초밥 먹음'
        changeTitle(titleCopy)
      }}>제목 수정</button>

      <button onClick={()=>{
        let sortTitle = [...a];
        sortTitle.sort
        changeTitle(sortTitle)
      }}>가나다순 정렬</button>
    </div>

  );
}

export default App;
