//eslint-disa 

import logo from './logo.svg';
import './App.css';
import { useState } from 'react';

function App() {

  let post = '강남 우동 맛집';
  let [a, b] = useState(['강남 우동 맛집','강남 초밥 맛집','강남 훠궈 맛집']) //자료 잠깐 저장, state 만드는법
  let [logo, setLogo] = useState('ReactBlog')
  //a : 강남 우동 맛집
  //b : state 변경을 도와주는 함수
  //변수 넣을 땐 중괄호

  //변수와 state의 차이점
  //일반 변수는 값자기 변경되면 html에 자동으로 반영 안됨
  //state는 갑자기 변경되면 html이 자동으로 재렌더링됨
  //그렇다고 온갖것을 다 state로 만들지는 말기, 자주 변경될 것 같은 것만
  return (
    <div className="App">
      <div className="black-nav">
        <h4>{logo}</h4>
      </div>
      <div className="list">
        <h4>{a[0]}</h4>
        <p>2월 17일 발행</p>
      </div>

      <div className="black-nav">
        <h4>{logo}</h4>
      </div>
      <div className="list">
        <h4>{a[1]}</h4>
        <p>2월 17일 발행</p>
      </div>

      <div className="black-nav">
        <h4>{logo}</h4>
      </div>
      <div className="list">
        <h4>{a[2]}</h4>
        <p>2월 17일 발행</p>
      </div>
    </div>
  );
}

export default App;
